mapper.select:
select count(*) from income
where income_date>=#{startDate}
and income_date<=#{endDate}

select *
from (select rownum rnum, product_code, auction_code, income, income_date
from (select * from income
where income_date>=#{startDate}
and income_date<=#{endDate}
 order by income_date desc)
where rownum<=#{endRow})
where rnum>=#{startRow}
