mapper.select:
select *
from (select rownum rnum, review_no,
product_code, member_id, review_title,
review_content, review_rating, original_img_name,
rename_img_name, review_date from (select * from review
where product_code=#{product_code}
order by review_no desc)
where rownum<=#{endRow})
where rnum>=#{startRow}
