mapper.select:
select *
from (select rownum rnum, review_no,
product_code, member_id, review_title,
review_content, review_rating, image_path, review_date from (select * from review
where product_code=#{product_code}
order by review_no desc)
where rownum<=#{endRow})
where rnum>=#{startRow}

mapper.update:
update review set
image_path=#{image_path},
review_title=#{review_title}, review_content=#{review_content}, review_rating=#{review_rating} where review_no=#{review_no}
